import re, codecs, binascii, base64
initial_string = ''
try:
    rot13_encode = codecs.getencoder('rot13')
    rot13_encoded = rot13_encode(initial_string)[0]
except LookupError:
    print('Flag \"Py4SE\" NOT found!')
    exit()
interim_string3 = int(''.join([str(ord(c8)) for c8 in rot13_encoded]).strip())
interim_string4 = hex(interim_string3).strip('0x')
interim_string5 = binascii.unhexlify(interim_string4)
final_string = str(base64.b64encode(interim_string5)).strip('b\'')
final_string = final_string.strip('\'')
print(final_string)
array_of_flags = re.findall('Py4SE', final_string)
if len(array_of_flags) > 0:
    print('Flag \"Py4SE\" found!')
else:
    print('Flag \"Py4SE\" NOT found!')